class KaluliArticle extends BaseKaluliArticle
{
    public function postInsert($event)
    {
        $message = array(
            'id' => $this->getId(),
            'type' => 'create',
            'channelType' => 'article'
        );
        $this->sendMqMessage($message);
        parent::postInsert($event);
    }

    public function preUpdate($event)
    {
        $new = $this->getModified();
        $modified = array_keys($new);

        $updateFields = array('title','intro','type','status','hits');
        if(array_intersect($updateFields,$modified)){
            $message = array(
                'id' => $this->getId(),
                'channelType' => 'article',
                'type' => 'update',
                'modified' => $modified
            );

            $this->sendMqMessage($message);
        }

        parent::preUpdate($event);
    }

    public function postDelete($event)
    {
        $message = array(
            'id' => $this->getId(),
            'type' => 'delete',
            'channelType' => 'article'
        );
        $this->sendMqMessage($message);
        parent::postDelete($event);
    }

    public function sendMqMessage($message)
    {
        $amqpParams = sfConfig::get("app_mabbitmq_options_kaluli");
        $connection = new AMQPConnection($amqpParams['params']['host'], $amqpParams['params']['port'], $amqpParams['params']['user'], $amqpParams['params']['pass'], $amqpParams['params']['vhost']);
        $channel = $connection->channel();
        $arguments = array(
            "x-dead-letter-exchange" => array("S", "amq.topic"),
            "x-message-ttl" => array("I", 2000),
            "x-dead-letter-routing-key" => array("S", "kaluli.article.detail")
        );
        $channel->queue_declare('kaluli_article_deferred', false, true, false, false, false, $arguments);

        $msg = new AMQPMessage(json_encode($message));
        $channel->basic_publish($msg, '', 'kaluli_article_deferred');
    }

    public function getContent(){
        $content = false;
        if($this->getId()){
            $attr = KaluliArticleAttrTable::getInstance()->createQuery()->where('article_id = ?',$this->getId())->fetchOne();
            $content = $attr['content'];
        }
        return $content;
    }

