 */
class KaluliLipinkaTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdLipinkaTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('KaluliLipinka');
    }


    public static function getFormatStatus($flag,$type = 'string'){
        $string = array(1=>'待审核',2=>'已通过',3=>'被拒绝');
        $html_one = array(1=>'<span class="c-ccc">待审核</span>',
            2=>'<span class="c-green">已通过</span>',
            3=>'<span class="c-red">被拒绝</span>');
        $type = $$type;
        if(!empty($type[$flag])) return $type[$flag];
        return false;
    }


    /**
     * 获取全部的数据
     */
    public static function  getAll($bind = array()) {
        $data = self::getInstance()->createQuery();
        //select
        if (!empty($bind['select'])){
            $data->select($bind['select']);
        } else {
            $data->select("*");
        }
        //where 简单判断  如果复杂 建议新写函数
        if(!empty($bind['where']) && count($bind['where']) > 0) {
            foreach($bind['where'] as $k=>$v) {
                $data->addWhere($v);
            }
        }
        //order
        if (!empty($bind['order'])){
            $data->orderBy($bind['order']);
        } else {
            $data->orderBy('id desc');
        }

        //limit
        if (!empty($bind['limit'])){
            $data->limit($bind['limit']);
        }

        if(!empty($bind['offset'])) {
            $data->offset($bind['offset']);
        }
        $data =  $data->fetchArray();
        if(!empty($bind['is_count'])) {
            $data = $data[0]['num'];
        }
        return $data;
    }


    /**
     * 删除数据
     */
    public static function del_one($id) {
        $data = self::getInstance()
            ->createQuery()
            ->select('*')
            ->andWhere('id = ?',$id)
            ->fetchOne();
        if(empty($data)) return false;
        return  $data->delete();
    }






}