class KaluliOrderWarelog extends BaseKaluliOrderWarelog
{
    //同步状态
    public static $SYNC_STATUS_NO = 0;  //订单没有录入管易
    public static $SYNC_STATUS_CREATE = 1; //订单创建
    public static $SYNC_STATUS_EXPRESS = 2;  //同步快递
    public static $SYNC_STATUS_CANCEL = 3;  //已撤销


    //同步状态数组
    public static $SYNC_STATUS_FORMAT = array(
        0 => '未同步',
        1 => '已同步，未发货',
        2 => '已同步，已发货',
        3 => '同步后撤销',
    );


    //同步 成功 失败
    public static $SYNC_OK = 1;
    public static $SYNC_FAIL = 0;


    //管易卡路里店铺ID
    public static $GUANYI_KALULI_ID = '002';



    public function getWareTypeString()
    {
        $wareData = KaluliWarehousesTable::getInstance()->getWare();
        $_type = $this->getWareType();

        if(isset($wareData[$_type])) return $wareData[$_type]['name'];
        return '未知仓库';
    }


    public function getStatusString()
    {
        switch($this->getStatus()){
            case 0:
                return '失败';
                break;
            case 1:
                return '成功';
                break;
            default:
                return '未知';
                break;
        }
    }

    public function getWareOrderTypeString()
    {
        $_type = $this->getWareOrderType();
        if(isset(self::$SYNC_STATUS_FORMAT[$_type])) {
            return self::$SYNC_STATUS_FORMAT[$_type];
        }
        return '未知';
    }















}
