class KllActivitySet extends BaseKllActivitySet
{


    public function save(Doctrine_Connection $conn = null) {

        //判断是否修改/创建集合
        $_is_write_set = $_POST['is_write_set'];
        if(!empty($_is_write_set)) {
            $_note_text = '';
            //先计算指定增加id
            if(!empty($_POST['roles']['add_id'])) {
                $_note_text .= '【追加指定ID】&nbsp;&nbsp;';
            }
            //判断是否查tag
            if(!empty($_POST['roles']['tag']) && count($_POST['roles']['tag']) > 0) {
                $_note_text .= '【追加指定tag】&nbsp;&nbsp;';
            }
             //判断是否有减id
            if(!empty($_POST['roles']['remove_id'])) {
                $_note_text .= '【减去指定id】&nbsp;&nbsp;';
            }
            $this->setNote($_note_text);
        }  //write end
        parent::save($conn);
        return $this->getId();
    }





}
