<?php

/**
 * KllArticleLabelTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class KllArticleLabelTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object KllArticleLabelTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('KllArticleLabel');
    }
    ////根据id获取标签
    public function getLabel($id)
    {
        $query= self::getInstance()->createQuery()
                ->select('id,name')
                ->whereIn('id',$id)
                ->fetchArray();
        return $query;  
    }
    public function findAllByBind($bind = []){
        $data = self::getInstance()->createQuery();
        if (!empty($bind['select'])){
            $data->select($bind['select']);
        } else {
            $data->select("*");
        }
        //where 简单判断  如果复杂 建议新写函数
        if(!empty($bind['where']) && count($bind['where']) > 0) {
            foreach($bind['where'] as $k=>$v) {
                $data->addWhere($v);
            }
        }
        //like
        if(!empty($bind['like'])){
            $data->like($bind['like']);
        }
        //limit
        if (!empty($bind['limit'])){
            $data->limit($bind['limit']);
        }

        if(!empty($bind['offset'])) {
            $data->offset($bind['offset']);
        }
        $data =  $data->fetchArray();

        if(!empty($bind['is_count'])) {
            $data = $data[0]['num'];
        }
        return $data;
    }

}