<?php

/**
 * KllBannerTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class KllBannerTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object KllBannerTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('KllBanner');
    }
    
    public function getAll($bind=[])
    {
        $query=  self::getInstance()->createQuery();
        ////
        if (!empty($bind['select'])){
            $query->select($bind['select']);
        } else {
            $query->select("*");
        }  
        //limit
        if (!empty($bind['limit'])){
            $query->limit($bind['limit']);
        }
        if(!empty($bind['offset'])) {
            $query->offset($bind['offset']);
        }        
        $data=$query->fetchArray();
        if(!empty($bind['is_count'])) {
            $data = $data[0]['num'];
        }        
        return $data;
    }
}