     */
    public static function del_one($id) {
        $data = self::getInstance()
            ->createQuery()
            ->select('*')
            ->andWhere('id = ?',$id)
            ->fetchOne();
        if(empty($data)) return false;
        return  $data->delete();
    }


    /**
     * 判断某个字段是否存在
     */
    public static function hasDataField($field,$value,$id) {
        $flag = self::getInstance()
            ->createQuery()
            ->select('id')
            ->andWhere($field.' = ?',$value);
        if(!empty($id)) {
            $flag->andWhere('id <> ?',$id);
        }
        return $flag->fetchOne();
