<?php

/**
 * KllCategoryTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class KllCategoryTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object KllCategoryTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('KllCategory');
    }
    
    public function fetchOneById($id)
    {
        $query= self::getInstance()->createQuery()
                ->where('id=?',$id)
                ->fetchOne();
        return $query;  
    }
    public function findCategory($bind = []){
        $data = self::getInstance()->createQuery();
        if (!empty($bind['select'])){
            $data->select($bind['select']);
        } else {
            $data->select("*");
        }
        //where 简单判断  如果复杂 建议新写函数
        if(!empty($bind['where']) && count($bind['where']) > 0) {
            foreach($bind['where'] as $k=>$v) {
                $data->addWhere($v);
            }
        }
        $data =  $data->fetchArray();
        return $data;
    }

    public static function getIndexNav(){
        $data = self::getInstance()
            ->createQuery()
            ->select('*')
            ->WhereIn("platform",array(2,3))
            ->fetchArray();

        if(empty($data)) return false;
        return $data;
    }
}