    public static function getInstance()
    {
        return Doctrine_Core::getTable('KllCpsLink');
    }


    public static function hasLink($_uid,$item_id,$_channel) {
        $info = self::getInstance()
            ->createQuery()
            ->select('*')
            ->andWhere('uid = ?',$_uid)
            ->andWhere('item_id = ?',$item_id)
            ->andWhere('channel = ?',$_channel)
            ->fetchOne();
        if(empty($info)) return false;
        $info = $info->toArray();
        return $info;
    }

    public static function getOne($_id,$is_toArray = false,$field = false) {
        $info = self::getInstance()
            ->createQuery()
            ->select('*')
            ->andWhere('id = ?',$_id)
            ->fetchOne();
        if(empty($info)) return false;
        if(!$is_toArray) return $info;
        $info = $info->toArray();
        if($field) {
            return $info[$field];
        } else {
            return $info;
        }
        return $info;
    }

    public static function getCacheByCodeOne($_code,$is_toArray = false,$field = false) {
        $info = self::getInstance()
            ->createQuery()
            ->select('*')
            ->andWhere('code = ?',$_code)
            ->fetchOne();
        if(empty($info)) return false;
        if(!$is_toArray) return $info;
        $info = $info->toArray();
        if($field) {
            return $info[$field];
        } else {
            return $info;
        }
        return $info;
    }




    /**
     * 通过自定义条件获取全部的数据
     * Abuout 梁天
     * ->from('TrdSpecial t')
     *   ->select('name,c.name ffff')->leftJoin('t.TrdSpecialCate c on t.cateid = c.id')
     */
    public static function  getAll($bind = array()) {
        $data = self::getInstance()->createQuery();
        //select
        if (!empty($bind['select'])){
            $data->select($bind['select']);
        } else {
            $data->select("*");
        }
        //leftJoin
        if (!empty($bind['leftJoin'])){
            $data->leftJoin($bind['leftJoin']);
        }
        //where 简单判断  如果复杂 建议新写函数
        if(!empty($bind['where']) && count($bind['where']) > 0) {
            foreach($bind['where'] as $k=>$v) {
                $data->addWhere($v);
            }
        }

        //whereIn 简单判断  如果复杂 建议新写函数
        if(!empty($bind['whereIn']) && count($bind['whereIn']) > 0) {
            foreach($bind['whereIn'] as $k=>$v) {
                $data->WhereIn($k,$v);
            }
        }

        //order
        if (!empty($bind['order'])){
            $data->orderBy($bind['order']);
        } else {
            $data->orderBy('id desc');
        }
        //limit
        if (!empty($bind['limit'])){
            $data->limit($bind['limit']);
        }

        if(!empty($bind['offset'])) {
            $data->offset($bind['offset']);
        }

        $data =  $data->fetchArray();
        if(!empty($bind['is_count'])) {
            $data = $data[0]['num'];
        }
        return $data;
    }


