<?php

/**
 * KllMemberBenefitsOrderTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class KllMemberBenefitsOrderTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object KllMemberBenefitsOrderTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('KllMemberBenefitsOrder');
    }
    public static function getAll($bind = []){

        $data = self::getInstance()->createQuery();
        if (!empty($bind['select'])){
            $data->select($bind['select']);
        } else {
            $data->select("*");
        }
        //where 简单判断  如果复杂 建议新写函数
        if(!empty($bind['where']) && count($bind['where']) > 0) {
            foreach($bind['where'] as $k=>$v) {
                $data->addWhere($v);
            }
        }
        //limit
        if (!empty($bind['limit'])){
            $data->limit($bind['limit']);
        }

        if(!empty($bind['offset'])) {
            $data->offset($bind['offset']);
        }
        $data =  $data->fetchArray();

        if(!empty($bind['is_count'])) {
            $data = $data[0]['num'];
        }
        return $data;
    }
    /**
     * 根据订单查询会员权益
     */
    public static function getBenefitsByOrder($order_number){
        $title = '无';
        $order = KllMemberBenefitsOrderTable::getInstance()->findOneByOrderNumber($order_number);
        if(!empty($order)){
            $mb_id = $order->getMbId();
            if($mb_id){
                $benefits = KllMemberBenefitsTable::getInstance()->findOneById($mb_id);
                $code = $benefits->getCode();
            }
        }
        return $code;

    }
}