<?php

/**
 * KllPurchaserAuthTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class KllPurchaserAuthTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object KllPurchaserAuthTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('KllPurchaserAuth');
    }
    public static function getOneAuth($uid) {
        $data = self::getInstance()->createQuery();
        $data->select("*")
             ->where("uid = ?",$uid)
             ->orderBy('create_time desc');
        $auth = $data->fetchArray();
        return $auth;
    }
    //随机获取一条
    public static function getOneRandAuth() {
        $data = self::getInstance()->createQuery();
        $data->select("*")
             ->orderBy('rand()')
             ->limit(1);
        $auth = $data->fetchArray();
        return $auth;
    }

}