<?php

/**
 * KllSpecialTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class KllSpecialTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object KllSpecialTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('KllSpecial');
    }
    
    ////根据分类id，获取对应的专题
    public function getSpecialByCid($id,$limit)
    {
//        $position='0-0';
        $query= self::getInstance()->createQuery()
                ->select('id,opt_uid,title,description,cid,is_home,add_time,update_time')
                ->whereIn('cid',($id))
//                ->andwhere('position=?',$position)
                ->limit($limit)
                ->fetchArray();
        return $query;
    }
    public function getSpecialById($id,$limit)
    {
        $query= self::getInstance()->createQuery()
                ->select('id,opt_uid,title,description,cid,position,add_time,update_time')
                ->whereIn('id',($id))
                ->limit($limit)
                ->fetchArray();
        return $query;
    }
    function findSpecialIDForCategory($bind = []){
        $data = self::getInstance()->createQuery();
        if (!empty($bind['select'])){
            $data->select($bind['select']);
        } else {
            $data->select("*");
        }
        //where 简单判断  如果复杂 建议新写函数
        if(!empty($bind['where']) && count($bind['where']) > 0) {
            foreach($bind['where'] as $k=>$v) {
                $data->addWhere($v);
            }
        }
        $data =  $data->fetchArray();
        return $data;
    }
}