<?php

/**
 * KllTrainingprogramTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class KllTrainingprogramTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object KllTrainingprogramTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('KllTrainingprogram');
    }

    /**
     * 根据分类获取训练计划
     * @param $categoryId
     */
    public static function getTransByCategory($categoryId,$page=1,$pageSize=5) {
        if(empty($categoryId)) return false;
        $data = self::getInstance()->createQuery()
               ->select("*")
               ->andWhere("category = ?",$categoryId)
               ->orderBy(" order desc")
               ->offset(($page-1)*$pageSize.",".$pageSize)
               ->limit($pageSize)
               ->fetchArray();
        if(empty($data)) return false;
        return $data;
    }

    public static function getTransCollection($id){
        if(empty($id)) return false;
        $data = self::getInstance()->createQuery()
            ->select("*")
            ->andWhere("id = ?",$id)
            ->fetchArray();
        if(empty($data)) return false;
        return $data[0];
    }




}