class TrdAdminRole extends BaseTrdAdminRole
{

    public function getRoleStatusHtml() {
        $type = $this->_get('role_status');
        switch($type){
            case 1:
                return '正常';
                break;
            case 2:
                return '禁用';
                break;
        }
    }

    public function getIsSuperString() {
        $type = $this->_get('is_super');
        switch($type){
            case 1:
                return '是';
                break;
            case 0:
                return '否';
                break;
        }
    }



}
