<?php

/**
 * TrdAlipayRedTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdAlipayRedTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdAlipayRedTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdAlipayRed');
    }
    
    public static function getOneMessage($money){
        return self::getInstance()->createQuery('p')
                ->where('p.money =?',$money)
                ->addwhere('p.status =?',0)
                ->limit(1)
                ->execute()
                ->toArray();
    }
}