<?php

/**
 * TrdAppActivityTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdAppActivityTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdAppActivityTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdAppActivity');
    }

    public static function getAllActivityCount()
    {
        $query = self::getInstance()->createQuery()->orderBy('id DESC');
        return $query->count();
    }

    public static function getActivity($offset = 0, $limit = 15)
    {
        $activities = self::getInstance()->createQuery()
            ->orderBy('start_time DESC, id DESC')
            ->limit($limit)
            ->offset($offset)
            ->execute();
        return $activities;
    }

    public function getAvailableActivity($limit = 3)
    {
        $activities = self::getInstance()->createQuery()
            ->where('is_delete = ?', 0)
            ->orderBy('start_time DESC, id DESC')
            ->limit($limit)
            ->execute();
        return $activities;
    }

    public function getActivityById($id)
    {
        if (empty($id)) {
            return false;
        }
        $activity = self::getInstance()->createQuery()
            ->where('id = ?', $id)
            ->addWhere('is_delete = ?', 0)
            ->fetchOne();
        return $activity;
    }
}