<?php

/**
 * TrdCategoryAllTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdCategoryAllTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdCategoryAllTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdCategoryAll');
    }

    public function getAll() {
        return self::getInstance()->createQuery('a')
            ->select('*')
            ->orderby("created_at asc")
            ->execute();
    }

    public function getAllWithNoShoe() {
        return self::getInstance()->createQuery('a')
            ->select('*')
            ->where("shortcut != 'shoe'")
            ->orderby("created_at asc")
            ->execute();
    }

    public function byName($name) {
        return self::getInstance()->createQuery('a')
            ->select('*')
            ->where("shortcut = ?", $name)
            ->fetchOne();
    }
}
