<?php

/**
 * TrdCategoryTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdCategoryTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdCategoryTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdCategory');
    }

    public function getAll() {
        return self::getInstance()->createQuery('a')
            ->select('*')
            ->orderby("is_default desc")
            ->execute();
    }

    public function getNav($names_arr) {
        foreach($names_arr as &$item) {
            $item = "'$item'";
        }

        $names_str = join(",", $names_arr);

        return self::getInstance()->createQuery('a')
            ->select('*')
            ->where("name in ({$names_str})")
            ->orderby("FIELD (`name`, $names_str)")
            ->execute();
    }
}
