<?php

/**
 * TrdColorTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdColorTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdColorTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdColor');
    }

    public function getAll() {
        return self::getInstance()->createQuery('a')
            ->select('*')
            ->execute();
    }
    
    public function getColorByName($name)
    {
      return self::getInstance()->createQuery()
                                ->where('name = ?', trim($name))
                                ->fetchOne();
    }
}
