<?php

/**
 * TrdCouponsDetailTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdCouponsDetailTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdCouponsDetailTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdCouponsDetail');
    }

    public static function getCouponsDetailByListId($list_id,$status = 0)
    {
        return self::getInstance()->createQuery()
                ->select('account,pass')
                ->where('list_id = ?',$list_id)
                ->andWhere('status = ?',$status)
                ->orderby('id asc')
                ->limit(1)
                ->fetchOne();
    }

}