<?php

/**
 * TrdDaigouShareHongbaoTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdDaigouShareHongbaoTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdDaigouShareHongbaoTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdDaigouShareHongbao');
    }

    /*
    * 根据订单号获取领取信息
    */
    public static function getByorderno($order_no){
      return self::getInstance()
          ->createQuery()
          ->select('order_no,hupu_uid,lipinka_amount,weixin_info,created_at')
          ->where('order_no = ?', $order_no)
          ->fetchArray();
    }


    /*
    * 根据订单号and 用户ID获取领取信息
    */
    public static function getByOU($order_no, $hupu_uid){
         return self::getInstance()
            ->createQuery()
            ->select('order_no,hupu_uid,lipinka_amount,weixin_info,created_at')
            ->where('order_no = ?', $order_no)
            ->andWhere('hupu_uid = ?', $hupu_uid)
            ->fetchArray();
    }
}