class TrdFindLikeTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdFindLikeTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdFindLike');
    }

    public static function getMessge($findId, $userId){
        $query = self::getInstance()->createQuery()
            ->where('f_id = ?', $findId)
            ->andWhere('hupu_uid = ?', $userId)
            ->andWhere('status = ?', 0)//æ­£å¸¸
            ->count();

        if($query){
            return true;
        }else{
            return false;
        }
    }
}