<?php

/**
 * TrdFindPraiseTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdFindPraiseTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdFindPraiseTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdFindPraise');
    }
    
    public static function getPraiseInfoByItemId($item_id,$uid){
        if (!$item_id || !$uid) return false;
        return self::getInstance()->createQuery('t')
                ->select('t.id')
                ->where('t.is_delete = 0')
                ->andWhere('t.item_id = ?',$item_id)
                ->andWhere('t.hupu_uid = ?',$uid)
                ->fetchOne();
    }

    public function getListByUid($uid)
    {
        return $this->createQuery()
            ->where('hupu_uid = ?', $uid)
            ->andWhere('is_delete = 0')
            ->execute();
    }
}