<?php

/**
 * TrdFindProductTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdFindProductTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdFindProductTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdFindProduct');
    }
    
    /*
     * 点赞
     */
    public static function getPraiseInfoByItemId($item_id,$uid){
        if (!$item_id) return false;
        $query =  self::getInstance()->createQuery('t')
                ->where('t.uid = ?',$uid)
                ->andwhere('t.item_id = ?',$item_id);
        return  $query->fetchOne();
    }
}