<?php

/**
 * TrdFindStoreTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdFindStoreTable extends Doctrine_Table
{

    /**
     * Returns an instance of this class.
     *
     * @return object TrdFindStoreTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdFindStore');
    }

    public function getMarts()
    {
        $q = $this->getInstance()->createQuery('j')
            ->select('j.id as id,j.name as mart')
            ->where('j.is_display = ?', 1)
            ->orderBy('j.sort asc');
        $res = $q->execute();
        return $res;
    }
    
    public function getAllMarts()
    {
        $q = $this->getInstance()->createQuery('j')
            ->select('j.id as id,j.name as mart')
            ->orderBy('j.sort asc');
        $res = $q->execute();
        return $res;
    }
}
