<?php

/**
 * TrdFunShopTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdFunShopTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdFunShopTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdFunShop');
    }

    public function getByCategoryName($name, $limit = 4)
    {
        return self::getInstance()
            ->createQuery()
            ->from("TrdFunShop as t")
            ->leftJoin("t.Category c")
            ->andWhere("c.name = ?", $name)
            ->addwhere('status = 0')
            ->limit($limit)
            ->execute();
    }
}
