class TrdGoods extends BaseTrdGoods
{
    public function postInsert($event)
    {
        $message = array(
            'id' => $this->getId(),
            'type' => 'create',
            'channelType' => 'goods'
        );
        $this->sendMqMessage($message);


        parent::postInsert($event);

    }

    public function preUpdate($event)
    {
        if($this->getIsDelete() == 0){
            $new = $this->getModified();
            $modified = array_keys($new);
            $updateFields = array(
                'name',
                'code',
                'pic',
                'price',
                'tags',
                'rootBrand',
                'childBrand',
                'rootCategory',
                'childCategory',
                'status',
                'type',
                'hits'
            );
            if (array_intersect($updateFields, $modified)) {#消息队列
                $message = array(
                    'id' => $this->getId(),
                    'type' => 'update',
                    'channelType' => 'goods',
                );
                $this->sendMqMessage($message);
            }
        }

        parent::preUpdate($event);
    }

    public function postDelete($event)
    {
        $message = array(
            'id' => $this->getId(),
            'type' => 'delete',
            'channelType' => 'goods'
        );
        $this->sendMqMessage($message);
        parent::postDelete($event);
    }

    public function preSave($event) {
//        $new = $this->getModified();

//        if ($this->isNew() || isset($new['hits'])){
//            //获取rank的公式
//            $this->setHits($this->getHits());
//        }
        parent::preSave($event);
    }

    public function sendMqMessage($message)
    {
        $amqpParams = sfConfig::get("app_mabbitmq_options_shihuo");
        $connection = new AMQPConnection($amqpParams['params']['host'], $amqpParams['params']['port'], $amqpParams['params']['user'], $amqpParams['params']['pass'], $amqpParams['params']['vhost']);
        $channel = $connection->channel();
        $arguments = array(
            "x-dead-letter-exchange" => array("S", "amq.topic"),
            "x-message-ttl" => array("I", 2000),
            "x-dead-letter-routing-key" => array("S", "shihuo.goods.detail")
        );
        $channel->queue_declare('trd_goods_deferred', false, true, false, false, false, $arguments);

        $msg = new AMQPMessage(json_encode($message));
        $channel->basic_publish($msg, '', 'trd_goods_deferred');

    }
}
