<?php

/**
 * TrdGoodsSupplierTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdGoodsSupplierTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdGoodsSupplierTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdGoodsSupplier');
    }
    public static function getCount($goodsId = null)
    {
        return self::getInstance()->createQuery()->andWhere('goods_id = ?',$goodsId)->andWhere('status = 0')->count();
    }

    public static function getMinPrice($goodsId)
    {
        $tmp =  self::getInstance()->createQuery()->select('min(price) as price')->andWhere('goods_id = ?',$goodsId)->fetchArray();
        return !empty($tmp[0]['price'])?$tmp[0]['price']:0;
    }

    //获取所有的渠道
    public static function getByGoodsId($goodsId = null)
    {
        return self::getInstance()->createQuery()->andWhere('goods_id = ?',$goodsId)->andWhere('status = 0')->fetchArray();
    }
}