 */
class TrdGroupTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdGroupTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdGroup');
    }
    
    /**
     *
     * 获取子分类下的groups
     * @param $cid 子分类的id
     * @return array 
     */
    public static function getGroupsByCid($cid) {
        $query = self::getInstance()->createQuery('m')
                ->where('menu_id = ?',$cid)
                ->orderBy('m.sort asc');
        
        return $query->fetchArray();
    }
}