class TrdGrouponPraise extends BaseTrdGrouponPraise
{

    public function postInsert($event)
    {
         if($this->getClientId())
         {
             $message = array();
             $message['uid'] = $this->getClientId();
             $message['actionid'] = $this->getGrouponId();
             $message['action'] = "grouponPraise";
             $this->sendMqMessage($message);
             parent::postInsert($event);
         }
    }

    private function sendMqMessage($message)
    {
        $amqpParams = sfConfig::get("app_mabbitmq_options_shihuo");
        $connection = new AMQPConnection($amqpParams['params']['host'], $amqpParams['params']['port'],$amqpParams['params']['user'], $amqpParams['params']['pass'], $amqpParams['params']['vhost']);
        $channel = $connection->channel();
        $arguments = array(
            "x-dead-letter-exchange" => array("S", "amq.topic"),
            "x-message-ttl" => array("I", 2000),
            "x-dead-letter-routing-key" => array("S", "shihuo.groupon.praise")
        );
        $channel->queue_declare('shihuo.groupon.praise.deferred_2', false, true, false, false, false, $arguments);
        $msg = new AMQPMessage(json_encode($message));
        $channel->basic_publish($msg, '', 'shihuo.groupon.praise.deferred_2');
    }



}
