<?php

/**
 * TrdHaitaoBlacklistTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdHaitaoBlacklistTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdHaitaoBlacklistTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdHaitaoBlacklist');
    }

    public static function houtaGetCount($search)
    {
        $query = self::getInstance()->createQuery();

        if(!empty($search['name']))
        {
            $query->andWhere('name like \'%'.$search['name'].'%\'');
        }
        if(!empty($search['number']))
        {
            $query->andWhere('number = \''.$search['number'].'\'');
        }
        return $query->count();
    }


    public static function houtaGetList($offset=0,$limit=20,$search)
    {
        $query = self::getInstance()->createQuery();
        if(!empty($search['name']))
        {
            $query->andWhere('name like \'%'.$search['name'].'%\'');
        }
        if(!empty($search['number']))
        {
            $query->andWhere('number = \''.$search['number'].'\'');
        }
        $query->orderBy('id desc')
            ->offset($offset)
            ->limit($limit);
        return $query->fetchArray();
    }
}