class TrdHaitaoGoods extends BaseTrdHaitaoGoods
{


    public function getMediumImage()
    {
        $attr = json_decode($this->getAttr(),1);
        return $attr['MediumImage']['URL'];
    }
    public function getVariationAttributes()
    {
        $attr = json_decode($this->getAttr(),1);
        if(!empty($attr['VariationAttributes']['VariationAttribute']))
        {
            return $attr['VariationAttributes']['VariationAttribute'];
        }else{
            return array();
        }
    }
    public function formatArray()
    {
        return array(
            'id' => $this->getId(),
            'title'=>$this->getTitle(),
            'goodsId'=>$this->getGoodsId(),
            'variationAttributes' => $this->getVariationAttributes(),
            'mediumImage' => $this->getMediumImage(),
            'code'=> $this->getCode()
        );
    }
}
