<?php

/**
 * TrdHomepageCheapTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdHomepageCheapTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdHomepageCheapTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdHomepageCheap');
    }

    public function getDefault()
    {
        return self::getInstance()->createQuery()
            ->where('status = 0')
            ->andWhere('is_default = 1')
            ->orderBy("id desc")
            ->limit(4)
            ->execute();
    }

    public function byCategoryName($name, $limit = 4) {
        return self::getInstance()->createQuery()
            ->from("TrdHomepageCheap as t")
            ->leftJoin("t.Category c")
            ->andWhere("c.name = ?", $name)
            ->andWhere('t.is_default = 0')
            ->orderby("created_at desc")
            ->limit($limit)
            ->execute();
    }

    public function byCategoryNames($names = array(), $limit = 4) {
        $sql = self::getInstance()->createQuery()
            ->from("TrdHomepageCheap as t")
            ->andWhere('t.is_default = 0')
            ->leftJoin("t.Category c")
            ->orderby("created_at desc")
            ->limit($limit);

        if(count($names) > 1) {
            $i = 1;
            foreach($names as $name) {
                if($i == 1) {
                    $sql->andWhere("c.name = ?", $name);
                } else {
                    $sql->orWhere("c.name = ?", $name);
                }
                $i++;
            }
        } else {
            $sql->andWhere("c.name = ?", $name);
        }

        return $sql->execute();
    }
}
