<?php

/**
 * TrdHomepageItemTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdHomepageItemTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdHomepageItemTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdHomepageItem');
    }

    public function byCategoryName($name, $limit = 5) {
        return self::getInstance()->createQuery()
            ->from("TrdHomepageItem as t")
            ->leftJoin("t.Category c")
            ->where('status = 0')
            ->andWhere("c.name = ?", $name)
            ->orderby("created_at desc")
            ->limit($limit)
            ->execute();
    }

}
