<?php

/**
 * TrdLotteryCardTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdLotteryCardTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdLotteryCardTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdLotteryCard');
    }


    //获取某个奖品的卡密
    public static function getUseCard($prize_id) {
        $data = self::getInstance()
            ->createQuery()
            ->select('*')
            ->andWhere('prize_id = ?',$prize_id)
            ->fetchArray();
        if(!empty($data)) return $data;
        return false;

    }

}