<?php

/**
 * TrdLotteryTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdLotteryTable extends Doctrine_Table
{

    public static  $SUCC_STATUS = '1';  //正常
    public static  $NOT_STATUS = '2';  //下架
    public static  $DEL_STATUS = '3';  //删除
    /**
     * Returns an instance of this class.
     *
     * @return object TrdLotteryTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdLottery');
    }

    public static function showSpecialStatus($show_flag,$type = 'string') {
        $string = array(1=>'上线',2=>'下线',3=>'删除');
        $html_one = array(1=>'<span class="c-green">上线</span>',2=>'<span class="c-red">下线</span>',3=>'<span class="c-red">删除</span>');
        $type = $$type;
        if(!empty($type[$show_flag])) return $type[$show_flag];
        return false;
    }
    /**
     * 获取全部的数据
     */
    public static function  getAll($bind = array()) {
        $data = self::getInstance()->createQuery();
        //select
        if (!empty($bind['select'])){
            $data->select($bind['select']);
        } else {
            $data->select("*");
        }
        //where 简单判断  如果复杂 建议新写函数
        if(!empty($bind['where']) && count($bind['where']) > 0) {
            foreach($bind['where'] as $k=>$v) {
                $data->addWhere($v);
            }
        }
        //order
        if (!empty($bind['order'])){
            $data->orderBy($bind['order']);
        } else {
            $data->orderBy('id desc');
        }

        //limit
        if (!empty($bind['limit'])){
            $data->limit($bind['limit']);
        }

        if(!empty($bind['offset'])) {
            $data->offset($bind['offset']);
        }
        $data =  $data->fetchArray();
        if(!empty($bind['is_count'])) {
            $data = $data[0]['num'];
        }
        return $data;
    }


    public static function getOne($id,$field = '') {
        $userInfo = self::getInstance()
            ->createQuery()
            ->select('*')
            ->andWhere('id = ?',$id)
            ->fetchOne();
        if(empty($userInfo)) return false;
        $userInfo = $userInfo->toArray();
        if(empty($field)) return $userInfo;
        return $userInfo[$field];
    }
    /**
     * 删除数据
     */
    public static function del_one($id) {
        $data = self::getInstance()->find($id);
        if(empty($data)) return false;
        $data->setStatus(self::$DEL_STATUS);
        $data->save();
        return true;
    }



}