<?php

/**
 * TrdLotteryUserTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdLotteryUserTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdLotteryUserTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdLotteryUser');
    }


    /**
     * 获取一条记录
     */
    public static function getOne($id,$field = '',$where = 'id',$is_toArray = true) {
        $userInfo = self::getInstance()
            ->createQuery()
            ->select('*')
            ->andWhere($where.' = ?',$id)
            ->fetchOne();
        if(empty($userInfo)) return false;
        if(!$is_toArray) return $userInfo;
        $userInfo = $userInfo->toArray();
        if(empty($field)) return $userInfo;
        return $userInfo[$field];
    }


    /**
     * 获取一条某抽奖活动下的用户信息
     */
    public static function  getLotteryOneUser($phone = '',$lottery = '',$is_toArray = true,$field = '') {
        $userInfo = self::getInstance()
            ->createQuery()
            ->select('*')
            ->andWhere("phone = ?",$phone)
            ->andWhere("lottery_id = ?",$lottery)
            ->fetchOne();
        if(empty($userInfo)) return false;
        if(!$is_toArray) return $userInfo;
        $userInfo = $userInfo->toArray();
        if(empty($field)) return $userInfo;
        return $userInfo[$field];
    }



    /**
     * 获取某个会员的卡密记录
     */
    public static function getAll($bind = array()) {
        $data = self::getInstance()->createQuery();
        //select
        if (!empty($bind['select'])){
            $data->select($bind['select']);
        } else {
            $data->select("*");
        }
        //where 简单判断  如果复杂 建议新写函数
        if(!empty($bind['where']) && count($bind['where']) > 0) {
            foreach($bind['where'] as $k=>$v) {
                $data->addWhere($v);
            }
        }
        //whereIn 简单判断  如果复杂 建议新写函数
        if(!empty($bind['whereIn']) && count($bind['whereIn']) > 0) {
            foreach($bind['whereIn'] as $k=>$v) {
                $data->WhereIn($k,$v);
            }
        }
        //order
        if (!empty($bind['order'])){
            $data->orderBy($bind['order']);
        } else {
            $data->orderBy('id desc');
        }
        //limit
        if (!empty($bind['limit'])){
            $data->limit($bind['limit']);
        }
        if(!empty($bind['offset'])) {
            $data->offset($bind['offset']);
        }
        $data =  $data->fetchArray();
        if(!empty($bind['is_count'])) {
            $data = $data[0]['num'];
        }
        return $data;
    }





}