<?php

/**
 * TrdMarketingActivityGroup
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 * 
 * @package    HC
 * @subpackage model
 * @author     HoopChina.com Dev Team
 * @version    SVN: $Id: Builder.php 7490 2010-03-29 19:53:27Z jwage $
 */
class TrdMarketingActivityGroup extends BaseTrdMarketingActivityGroup
{
    public function postInsert($event)
    {
        $message = array(
            'id' => $this->getItemId(),
            'type' => 'create',
            'channelType' => 'product_marketing'
        );
        $this->sendMqMessage($message);
        parent::postInsert($event);
    }

    public function preUpdate($event)
    {
        $new = $this->getModified();
        $modified = array_keys($new);

        $updateFields = array('activity_id','item_id');
        if(array_intersect($updateFields,$modified)){
            //如果是删除了就不处理了
            $message = array(
                'id' => $this->getItemId(),
                'channelType' => 'product_marketing',
                'modified' => $modified
            );

            $this->sendMqMessage($message);
        }

        parent::preUpdate($event);
    }

    public function postDelete($event)
    {
        $message = array(
            'id' => $this->getItemId(),
            'type' => 'delete',
            'channelType' => 'product_marketing'
        );
        $this->sendMqMessage($message);
        parent::postDelete($event);
    }

    public function sendMqMessage($message)
    {
        $amqpParams = sfConfig::get("app_mabbitmq_options_shihuo");
        $connection = new AMQPConnection($amqpParams['params']['host'], $amqpParams['params']['port'], $amqpParams['params']['user'], $amqpParams['params']['pass'], $amqpParams['params']['vhost']);
        $channel = $connection->channel();
        $arguments = array(
            "x-dead-letter-exchange" => array("S", "amq.topic"),
            "x-message-ttl" => array("I", 2000),
            "x-dead-letter-routing-key" => array("S", "shihuo.product.marketing.detail")
        );
        $channel->queue_declare('product_marketing_deferred', false, true, false, false, false, $arguments);

        $msg = new AMQPMessage(json_encode($message));
        $channel->basic_publish($msg, '', 'product_marketing_deferred');
    }

}
