class TrdMartOrderInfo extends BaseTrdMartOrderInfo
{
    public function getDifference(){
        if ($this->getBusiness() == '美国亚马逊'){
            return round($this->getShPrice()-$this->getShMarketingFee() - $this->getTotalPrice()*6.5)*100/100;
        }
        return 0;
    }
}
