<?php

/**
 * TrdMobileAdTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdMobileAdTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdMobileAdTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdMobileAd');
    }

    public static function getAllAdCount()
    {
        $query = self::getInstance()->createQuery()->orderBy('id DESC');

        return $query->count();
    }

    public static function getAd($offset = 0, $limit = 15)
    {
        $ads = self::getInstance()->createQuery()
            ->orderBy('id DESC')
            ->limit($limit)
            ->offset($offset)
            ->fetchArray();

        return $ads;
    }

    public function getHomeAd()
    {
        $time = time();
        $ad = self::getInstance()->createQuery()
            ->where('is_cancel = ?', 0)
            ->addWhere('start_time <= ?', date('Y-m-d H:i:s', $time))
            ->addWhere('end_time >= ?', date('Y-m-d H:i:s', $time))
            ->orderBy('updated_at desc')
            ->fetchOne();

        $ad && $ad = $ad->toArray();

        return $ad;
    }
}