<?php

/**
 * TrdModelTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdModelTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdModelTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdModel');
    }
    
    /**
     *
     * 获取类型下的型号
     * @param $brand 品牌的id
     * @return array 
     */
    public static function getModelByBid($brand) {
        $query = self::getInstance()->createQuery('m')
                ->where('brand_id = ?',$brand)
                ->orderBy('m.id asc');
        
        return $query->fetchArray();
    }
    
    /**
     *
     * 根据id获取型号
     * @param id
     * @return array 
     */
    public static function getModelByid($id) {
        $query = self::getInstance()->createQuery('m')
                ->where('id = ?',$id)
                ->orderBy('m.id asc');
        
        return $query->fetchOne();
    }
}