<?php

/**
 * TrdNewsTagTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdNewsTagTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdNewsTagTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdNewsTag');
    }
    
     public static function getTrdProductTagsByNewsId($news_id){
      if (!$news_id) return array();
      return self::getInstance()->createQuery('m')
                ->setResultCacheLifeSpan(60 * 10)
                ->useResultCache()
                ->leftJoin('m.TrdProductTag t')
                ->where('m.trd_news_id=? ', $news_id)
                ->orderBy('m.is_default desc')
                ->fetchArray();
  }

}