 */
class TrdProductCollocation extends BaseTrdProductCollocation
{
    public function save(Doctrine_Connection $conn = null)
    {
        parent::save($conn);
        //同步主表状态
        $productAttr = TrdProductAttrTable::getInstance()->find($this->getProductId());
        if ($productAttr) {
            if ($this->getShaiwuAttr()){
                $shaiwu_attr = json_decode($this->getShaiwuAttr(), true);
                if ($shaiwu_attr){
                    $num = count($shaiwu_attr);
                    $productAttr->setShaiwuCount($num);
                    $productAttr->save();
                }
            } else{
                $productAttr->setShaiwuCount(0);
                $productAttr->save();
            }

        }
        return $this->getId();
    }
}
