<?php

/**
 * TrdSearchHistoryTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdSearchHistoryTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdSearchHistoryTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdSearchHistory');
    }
    
    public static function getOneByWord($word,$time,$type = 0){
        $query = self::getInstance()->createQuery('m')
                ->where('m.word = ?',$word)
                ->andWhere('m.type = ?',$type)
                ->andWhere('m.time = ?',$time);
        return $query->fetchOne();
    }
}