<?php

/**
 * TrdSeoNewsClusterTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdSeoNewsClusterTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdSeoNewsClusterTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdSeoNewsCluster');
    }

    /*分页获取信息*/
    public static function getInfoByIds($ids){
        return $query =  self::getInstance()->createQuery()
            ->select('text,intro')
            ->where("id in (" . $ids . ") ")
            ->orderBy("FIELD(`id`,".$ids.")" )
            ->execute();

    }
}