<?php

/**
 * TrdShaiwuStarTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdShaiwuStarTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdShaiwuStarTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdShaiwuStar');
    }

    public static function houtaGetCount()
    {
        return self::getInstance()->createQuery()->count();
    }


    public static function houtaGetList($offset=0,$limit=20)
    {
        $query = self::getInstance()->createQuery()
            ->orderBy('id desc')
            ->offset($offset)
            ->limit($limit);
        return $query->fetchArray();
    }

    public static function setStarByUid($uid,$type = 1)
    {
        if(empty($uid)) return false;
        if( $type == 0 )
        {
            # 若当前有2次以上晒物之星，不必取消晒物之星
            $userCount = self::getInstance()->createQuery()->where('uid = ?',$uid)->count();
            if($userCount>1)
            {
                return false;
            }
        }
        $connection = Doctrine_Manager::getInstance()->getConnection('trade');
        $query = "UPDATE trd_shaiwu_product SET is_star = '{$type}' WHERE author_id = '{$uid}' ";
        $statement = $connection->execute($query);
        $resultset = $statement->execute();
        return $resultset;
    }

    public static function getStarByUid($uid)
    {
        if(empty($uid)) return false;
        $r = self::getInstance()->createQuery()
           ->where('uid = ?',$uid)->fetchOne();
        return $r;
    }

    public static function getStar()
    {
        $data = CacheModel::getCache();
        if (empty($data))
        {
            $data = self::getInstance()->createQuery()
                ->orderBy('id desc')->fetchArray();
            if (empty($data)) {
                return false;
            }
            $data = $data[0];
            CacheModel::setCache($data, 3600);
        }
        return $data;
    }
}