<?php

/**
 * TrdSizeTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdSizeTable extends Doctrine_Table
{
    const SEX_FEMALE = 0;
    const SEX_MALE = 1;    
    const SEX_BOTH = 2;
    const SEX_SHEMALE = 3; // :P
    
    /**
     * Returns an instance of this class.
     *
     * @return object TrdSizeTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdSize');
    }

    public function getAll() {
        return self::getInstance()->createQuery('a')
            ->select('*')
            ->execute();
    }
    
    public function getSize($size, $sex = self::SEX_BOTH)
    {
      $query = self::getInstance()->createQuery()
                                  ->where('value = ?', $size);
                                
      if ($sex == self::SEX_MALE || $sex == self::SEX_FEMALE)
      {
        $query->andWhere('sex = ?', $sex);
      }
                                                                
      return $query->execute();
    }    

    public function getSizesBySex($sex = 1)
    {
      $query = self::getInstance()->createQuery(); 
      if ($sex == self::SEX_MALE || $sex == self::SEX_FEMALE)
      {
        $query->andWhere('sex = ?', $sex);
      }
                                                                
      return $query->execute();
    }    
}
