class TrdSpecialVanclMatch extends BaseTrdSpecialVanclMatch
{
    public function __toString() {
        return 'ID:'.$this->getId().'--'.$this->getHomeTeam().'vs'.$this->getAwayTeam().'--'.  date('Y-m-d',strtotime($this->getEndTime()));
    }
    public function getHomeLogoPath(){
        return '/'.basename(sfConfig::get('sf_upload_dir')).'/shihuo/special/vancal/match/'.$this->getHomeLogo();
    }
    public function getAwayLogoPath(){
        return '/'.basename(sfConfig::get('sf_upload_dir')).'/shihuo/special/vancal/match/'.$this->getAwayLogo();
    }    
}
