    public function byName($name) {
        return self::getInstance()->createQuery('a')
            ->select('*')
            ->where("name = ?", $name)
            ->fetchOne();
    }

    public function autoSave($name) {
        if(!($rs = $this->byName($name))) {
            $TrdTags = New TrdTags();
            $TrdTags->setName($name);
            $TrdTags->save();
        }
    }

    public function getTag($name) {
        //确保有标签存在
        $this->autoSave($name);

        return $this->byName($name);
    }
