<?php

/**
 * TrdUserActivityTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdUserActivityTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdUserActivityTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdUserActivity');
    }

    /*
    * 是否有用户活动
    */
    public static function isHasActivity($typeId = 1, $time = '', $fileterId = false, $isCache = true ){
        $time = !$time ? date('Y-m-d H:i:s',time()) : $time;
        $activity = self::getInstance()->createQuery();

        if($isCache){
            $activity = $activity->setResultCacheLifeSpan(60)->useResultCache();
        }
        if($fileterId){
            $activity = $activity->andWhere('id != ?', $fileterId);
        }

        $activity = $activity->andWhere('type = ?', $typeId)       //类型ID
            ->andwhere('start_time <= ?', $time)
            ->andWhere('end_time >= ?', $time)
            ->andWhere('status = ?',0)
            ->orderby('created_at DESC')
            ->fetchOne();

        if($activity){
            return $activity;
        }else{
            return false;
        }
    }


}